package LeetCodeII;

import java.util.*;

/**
 *  * Given a string S and a string T, count the number of distinct subsequences of T in S.

 A subsequence of a string is a new string which is formed from the original string by deleting some (can be none) of the characters without disturbing the relative positions of the remaining characters. (ie, "ACE" is a subsequence of "ABCDE" while "AEC" is not).

 Here is an example:
 S = "rabbbit", T = "rabbit"

 Return 3.
 *
 * Created by Thanakorn on 7/11/16.
 */
public class DistinctSubsequences {

    public static void main(String[] args) {
        DistinctSubsequences distinctSubsequences = new DistinctSubsequences();
        String s = "rabbbit";
        String t = "rabbit";
        System.out.println(distinctSubsequences.numDistinct(s, t));  // 3

        s = "";
        t = "rwmimatmhydhbujebqehjprarwfkoebcxxqfktayaaeheys";
        System.out.println(distinctSubsequences.numDistinct(s, t));
    }

    public int numDistinct(String s, String t) {
        if (s == null || t == null) {
            return 0;
        }
        Map<Integer, Map<Integer, Integer>> memo = new HashMap<>();
        return numDistinctHelper(s, t, 0, 0, memo);
    }

    private int numDistinctHelper(String s, String t,
                                   int sIndex, int tIndex,
                                   Map<Integer, Map<Integer, Integer>> memo) {

        if (s.length() - sIndex < t.length() - tIndex) {
            return 0;
        }

        if (!memo.containsKey(sIndex)) {
            memo.put(sIndex, new HashMap<>());
        }
        if (sIndex >= s.length() || tIndex >= t.length()) {
            int count;
            if (sIndex >= s.length() && tIndex >= t.length()) {
                count = 1;
            } else if (sIndex >= s.length()) {
                count = 0;
            } else {
                count = 1;
            }
            memo.get(sIndex).put(tIndex, count);
            return count;
        }

        if (memo.containsKey(sIndex) && memo.get(sIndex).containsKey(tIndex)) {
            return memo.get(sIndex).get(tIndex);
        }

        int count = 0;
        if (s.charAt(sIndex) == t.charAt(tIndex)) {
            count += numDistinctHelper(s, t, sIndex + 1, tIndex + 1, memo);
        }
        count += numDistinctHelper(s, t, sIndex + 1, tIndex, memo);

        memo.get(sIndex).put(tIndex, count);
        return count;
    }

}
